{

    private $client;
    function __construct()
    {
        $this->client = new \swoole_client(SWOOLE_SOCK_TCP);
    }
    public function send($data)
    {
        if (!$this->client->connect('127.0.0.1', 9501)) {
            die('connect failed.');
        }
        return $this->client->send(json_encode($data));
    }
}
